        setTimeout(loadVoices, 100); // Retry until voices are loaded
      }
    };
    loadVoices();
  }, []);

  const startListening = () => {
    setListening(true);
    SpeechRecognition.startListening({ language: "hi-IN", continuous: true });
  };

